CKEDITOR.editorConfig = function( config ) {
	config.language = 'vi';
	config.uiColor = '#f2f3f8';	
    config.filebrowserBrowseUrl = '/file-management';
    config.filebrowserImageBrowseUrl = '/file-management?Type=Images';
    config.filebrowserWindowWidth = '1000';
    config.filebrowserWindowHeight = '700';
};
